import styled from 'styled-components';

// Template literals
export const Title = styled.h1`
  font-size: 49px;
  color: #3a3a3a;
  max-width: 450px;
  line-height: 56px;

  margin-top: 80px;
`;

export const Form = styled.form`
  margin-top: 40px;
  max-width: 700px;

  display: flex;

  input {
    flex: 1;
    height: 70px;
  }

  button {
    width: 210px;
    height: 70px;
  }
`;
